import { useState } from 'react';
import Input from './components/input/Input';
import Notes from './components/notes/Notes';
import { title } from 'process';

type Note = { id: number; title: string; content: string };
const App = () => {
  const notesData: Note[] = [
    {
      id: 1,
      title: 'test note 1',
      content: 'bla bla note1'
    },
    {
      id: 2,
      title: 'test note 2 ',
      content: 'bla bla note2'
    },
    {
      id: 3,
      title: 'test note 3',
      content: 'bla bla note3'
    },
    {
      id: 4,
      title: 'test note 4 ',
      content: 'bla bla note4'
    },
    {
      id: 5,
      title: 'test note 5',
      content: 'bla bla note5'
    },
    {
      id: 6,
      title: 'test note 6',
      content: 'bla bla note6'
    }
  ];
  const [title, setTitle] = useState<string>('');
  const [content, setContent] = useState<string>('');
  const [notes, setNotes] = useState<Note[]>(notesData);

  const handleFormSubmission = (event: React.FormEvent) => {
    event.preventDefault();
    console.log('titile', title);
    console.log('content', content);
    setNotes([{ id: notesData.length + 1, title, content }]);
    console.log('data', notesData);
  };
  return (
    <div className="w-full flex">
      <form className="flex flex-col w-1/4" onSubmit={handleFormSubmission}>
        <Input
          value={title}
          onChange={(event) => setTitle(event.target.value)}
          className="mb-8 p-2"
          placeholder="Title"
          required
        />
        <textarea
          value={content}
          onChange={(event) => setContent(event.target.value)}
          placeholder="Content"
          required
          rows={10}
          className="border focus:outline-none p-2 rounded-md"
        />
        <button
          type="submit"
          className="h-10 text-white bg-[#409ab8] rounded-md mt-4"
        >
          Add Note
        </button>
      </form>
      <div className="w-3/4 ml-4 grid grid-cols-2 gap-4">
        {notesData.map((item) => (
          <Notes title={item.title} content={item.content} key={item.id} />
        ))}
      </div>
    </div>
  );
};

export default App;
