import { createApi, fetchBaseQuery } from '@reduxjs/toolkit/dist/query/react';

const baseNotesApi = createApi({
  reducerPath: 'api',
  baseQuery: fetchBaseQuery({ baseUrl: 'http://localhost:5000' }),
  endpoints: () => ({})
});

export default notesApi;
