import apiSlice from '../core/baseApi';

const notesApi = apiSlice.injectEndpoints({
  endpoints: (builder) => ({
    getAllNotes: builder.query({
      query: () => `/api/notes`
    }),
    createNote: builder.mutation({ query: () => '/api/notes' })
  })
});

export const { useGetAllNotesQuery, useLazyGetAllNotesQuery } = notesApi;
