import { useState } from 'react';
import Input from './components/input/Input';
import Notes from './components/notes/Notes';

type Note = { id: number; title: string; content: string };
const App = () => {
  const notesData: Note[] = [
    {
      id: 1,
      title: 'test note 1',
      content: 'bla bla note1'
    },
    {
      id: 2,
      title: 'test note 2 ',
      content: 'bla bla note2'
    },
    {
      id: 3,
      title: 'test note 3',
      content: 'bla bla note3'
    },
    {
      id: 4,
      title: 'test note 4 ',
      content: 'bla bla note4'
    },
    {
      id: 5,
      title: 'test note 5',
      content: 'bla bla note5'
    },
    {
      id: 6,
      title: 'test note 6',
      content: 'bla bla note6'
    }
  ];
  const [title, setTitle] = useState<string>('');
  const [content, setContent] = useState<string>('');
  const [notes, setNotes] = useState<Note[]>(notesData);
  const [selectedNote, setSelectedNote] = useState<Note | null>(null);

  const handleAddNote = (event: React.FormEvent) => {
    event.preventDefault();
    setNotes([{ id: notesData.length + 1, title, content }, ...notes]);
    setTitle('');
    setContent('');
  };

  const handleNoteClick = (note: Note) => {
    setSelectedNote(note);
    setTitle(note.title);
    setContent(note.content);
  };

  const handelUpdateNote = () => {
    const updatedNoteList = notes.map((item) =>
      item.id === selectedNote?.id ? selectedNote : item
    );
    setNotes(updatedNoteList);
  };

  const handleCancel = () => {
    setSelectedNote(null);
    setTitle('');
    setContent('');
  };

  const handleDeleteNote = (note: Note) => {
    console.log(note);
    const updatedNoteList=notes.filter((item)=>item.id)
  };

  return (
    <div className="flex w-full">
      <form
        className="flex w-1/4 flex-col"
        onSubmit={selectedNote ? handelUpdateNote : handleAddNote}
      >
        <Input
          value={title}
          onChange={(event) => setTitle(event.target.value)}
          className="mb-8 p-2"
          placeholder="Title"
          required
        />
        <textarea
          value={content}
          onChange={(event) => setContent(event.target.value)}
          placeholder="Content"
          required
          rows={10}
          className="rounded-md border p-2 focus:outline-none"
        />
        {selectedNote ? (
          <div className="w-full">
            <button
              type="submit"
              className="mt-4 h-10 w-1/2 rounded-md bg-[#409ab8] text-white"
              onClick={handelUpdateNote}
            >
              Save
            </button>
            <button
              type="submit"
              className="mt-4 h-10 w-1/2 rounded-md bg-[#409ab8] text-white"
              onClick={handleCancel}
            >
              Cancel
            </button>
          </div>
        ) : (
          <button
            type="submit"
            className="mt-4 h-10 rounded-md bg-[#409ab8] text-white"
          >
            Add Note
          </button>
        )}
      </form>
      <div className="ml-4 grid w-3/4 grid-cols-2 gap-4">
        {notes.map((item) => (
          <div role="presentation" onClick={() => handleNoteClick(item)}>
            <Notes
              title={item.title}
              content={item.content}
              key={item.id}
              handleCancelClick={() => handleDeleteNote(item)}
            />
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
