import { useEffect, useState } from 'react';
import Input from './components/input/Input';
import Notes from './components/notes/Notes';

type Note = { id: number; title: string; content: string };

const notesData: Note[] = [
  {
    id: 1,
    title: 'test note 1',
    content: 'bla bla note1'
  },
  {
    id: 2,
    title: 'test note 2 ',
    content: 'bla bla note2'
  },
  {
    id: 3,
    title: 'test note 3',
    content: 'bla bla note3'
  },
  {
    id: 4,
    title: 'test note 4 ',
    content: 'bla bla note4'
  },
  {
    id: 5,
    title: 'test note 5',
    content: 'bla bla note5'
  },
  {
    id: 6,
    title: 'test note 6',
    content: 'bla bla note6'
  }
];

const App = () => {
  const [title, setTitle] = useState<string>('');
  const [content, setContent] = useState<string>('');
  const [notes, setNotes] = useState<Note[]>(notesData);
  const [selectedNote, setSelectedNote] = useState<Note | null>(null);

  const handleAddNote = async (event: React.FormEvent) => {
    event.preventDefault();
    // try {
    //   const response = await fetch('http://localhost:5000/api/notes', {
    //     method: 'POST',
    //     headers: { 'Content-Type': 'application/json' },
    //     body: JSON.stringify({ title, content })
    //   });
    //   const newNote = await response.json();
    //   console.log('ew', newNote);
    //   setNotes([newNote, ...notes]);
    //   setTitle('');
    //   setContent('');
    // } catch (error) {
    //   console.log(error);
    // }
    setNotes([{ id: notes.length, title, content }, ...notes]);
    setTitle('');
    setContent('');
  };
  console.log('note', notes);
  const handleNoteClick = (note: Note) => {
    setSelectedNote(note);
    setTitle(note.title);
    setContent(note.content);
  };

  const handelUpdateNote = async () => {
    // try {
    //   const response = await fetch(
    //     `http://localhost:5000/api/notes/${selectedNote?.id}`,
    //     {
    //       method: 'PUT',
    //       headers: { 'Content-Type': 'application/json' },
    //       body: JSON.stringify({ title, content })
    //     }
    //   );
    //   const updatedNote = await response.json();
    //   const updatedNoteList = notes.map((item) =>
    //     item.id === selectedNote?.id ? updatedNote : item
    //   );
    //   setNotes(updatedNoteList);
    // } catch (error) {
    //   console.log(error);
    // }
  };

  const handleCancel = () => {
    setSelectedNote(null);
    setTitle('');
    setContent('');
  };

  const handleDeleteNote = async (
    event: React.MouseEvent<HTMLElement>,
    note: Note
  ) => {
    event.stopPropagation();
    try {
      await fetch(`http://localhost:5000/api/notes/${note.id}`, {
        method: 'DELETE'
      });
      const updatedNoteList = notes.filter((item) => item.id !== note.id);
      setNotes(updatedNoteList);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/notes');
        const note: Note[] = await response.json();
        setNotes(note);
      } catch (error) {
        console.log(error);
      }
    };
    fetchData();
  }, []);

  return (
    <div className="flex w-full">
      <form
        className="flex w-1/4 flex-col"
        onSubmit={selectedNote ? handelUpdateNote : handleAddNote}
      >
        <Input
          value={title}
          onChange={(event) => setTitle(event.target.value)}
          className="mb-8 p-2"
          placeholder="Title"
          required
        />
        <textarea
          value={content}
          onChange={(event) => setContent(event.target.value)}
          placeholder="Content"
          required
          rows={10}
          className="rounded-md border p-2 focus:outline-none"
        />
        {selectedNote ? (
          <div className="w-full">
            <button
              type="submit"
              className="mt-4 h-10 w-1/2 rounded-md bg-[#409ab8] text-white"
              onClick={handelUpdateNote}
            >
              Save
            </button>
            <button
              type="submit"
              className="mt-4 h-10 w-1/2 rounded-md bg-[#409ab8] text-white"
              onClick={handleCancel}
            >
              Cancel
            </button>
          </div>
        ) : (
          <button
            type="submit"
            className="mt-4 h-10 rounded-md bg-[#409ab8] text-white"
          >
            Add Note
          </button>
        )}
      </form>
      <div className="ml-4 grid w-3/4 grid-cols-2 gap-4">
        {notes.map((item) => (
          <div
            role="presentation"
            key={item.id}
            onClick={() => handleNoteClick(item)}
          >
            <Notes
              title={item.title}
              content={item.content}
              key={item.id}
              handleCancelClick={(e: React.MouseEvent<HTMLElement>) =>
                handleDeleteNote(e, item)
              }
            />
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
